package day03increment_decrement_operators;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int variable = 1;
		switch (variable){
			case 6 :
				System.out.print("P");
		    case 2 :
		    case 3 :
		    	System.out.print("Q");
		    //break;
		    case 4 :
		    	System.out.print("R");
		    default :
		    	System.out.print("S");
		}
	}

}
